import { hideScrollbar, invisible } from "constants/DefaultTheme";
import { lightenColor, darkenColor } from "widgets/WidgetUtils";
import { FontStyleTypes } from "constants/WidgetConstants";
import { Classes } from "@blueprintjs/core";
import type { TableVariant } from "../constants";
import { TableVariantTypes } from "../constants";
import { Layers } from "constants/Layers";

const BORDER_RADIUS = "border-radius: 4px;";
const HEADER_CONTROL_FONT_SIZE = "12px";

export const TableWrapper = styled.div<{
  width: number;
  height: number;
  tableSizes: TableSizes;
  accentColor: string;
  backgroundColor?: Color;
  triggerRowSelection: boolean;
  isHeaderVisible?: boolean;
  borderRadius: string;
  boxShadow?: string;
  borderColor?: string;
  borderWidth?: number;
  isResizingColumn?: boolean;
  variant?: TableVariant;
  isAddRowInProgress: boolean;
  multiRowSelection?: boolean;
}>`
  width: 100%;
  height: 100%;
  background: white;
  border-style: solid;
  border-width: ${({ borderWidth }) => `${borderWidth}px`};
  border-color: ${({ borderColor }) => borderColor};
  border-radius: ${({ borderRadius }) => borderRadius};
  box-shadow: ${({ boxShadow }) => `${boxShadow}`} !important;
  box-sizing: border-box;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  overflow: hidden;
  .simplebar-track {
    opacity: 0.7;
    &.simplebar-horizontal {
-      /* this height moves the scrollbar up and down */
      height: ${TABLE_SCROLLBAR_HEIGHT}px;
      .simplebar-scrollbar {
-        /*this actual height of the scrollbar */
-        height: 8px;
+        height: 5px;
       }
       &.simplebar-hover {
+        height: 10px;
         & .simplebar-scrollbar {
-          transition: height 0.15s ease-in-out;
           height: 8px;
         }
       }
     }

    &.simplebar-vertical {
      direction: rtl;
      top: ${(props) => props.tableSizes.TABLE_HEADER_HEIGHT - 10}px;
      width: ${TABLE_SCROLLBAR_WIDTH}px;
      &.simplebar-hover {
        width: 10px;
        & .simplebar-scrollbar {
          width: 11px;
        }
      }
    }
  }
  .tableWrap {
    height: 100%;
    display: block;
    position: relative;
    width: ${({ width }) => width}px;
    overflow: auto hidden;
    &.virtual {
      ${hideScrollbar};
    }
  }
  .table {
    border-spacing: 0;
    color: ${Colors.THUNDER};
    position: relative;
    display: table;
    width: 100%;
    ${hideScrollbar};
    .tbody {
      height: fit-content;
      width: fit-content;
    }
    .tr {
      cursor: ${(props) => props.triggerRowSelection && "pointer"};
      background: ${Colors.WHITE};
      &.selected-row {
        background: ${({ accentColor }) =>
          `${lightenColor(accentColor)}`} !important;

        &:hover {
          background: ${({ accentColor }) =>
            `${lightenColor(accentColor, "0.9")}`} !important;
        }
      }

      ${(props) => {
        if (!props.isAddRowInProgress) {
          return `&:hover {
            background: var(--wds-color-bg-hover) !important;
          }`;
        }
      }}
      &.new-row {
        background: ${({ accentColor }) =>
          `${lightenColor(accentColor)}`} !important;
      }
    }
    .th,
    .td {
      margin: 0;
      position: relative;
      border-right: ${(props) =>
        props.variant === TableVariantTypes.DEFAULT ||
        props.variant === undefined ||
        props.isResizingColumn
          ? "1px solid var(--wds-color-border-onaccent)"
          : "none"};
      position: relative;
