    form.append('foo', '123');

    const {data, headers, status} = await axios.post('https://httpbin.org/post', form, {
      httpVersion: 2,
      http2Options: {
        // rejectUnauthorized: false,
        // sessionTimeout: 1000
      }
      onUploadProgress(e) {
        console.log('upload progress', e);
      },
      onDownloadProgress(e) {
        console.log('download progress', e);
      },
      responseType: 'arraybuffer'
    });
```

