-
-      if (relationsToInsert.length > 0) {
-        await insertRelationsWithDuplicateHandling({
-          trx,
-          tableName: joinTable.name,
-          relations: relationsToInsert,
-          context: {
-            reason: 'duplicate-relation-check',
-            sourceUid: uid,
-            targetUid,
-          },
-        });
-      }
-    }
-  }
-}
